use corpore
SELECT TOP 1 
KA.CODORDEM,KA.IDATVORDEM,Z.CODIGOMP,T.IDPRD,E.CUSTOMEDIO,E2.CODLOC,Z.CODCOLIGADA,Z.CODFILIAL,K.CODCCUSTO   FROM ZMDPLANOAPROVEITAMENTOCORTE Z  INNER JOIN KORDEM K ON  K.CODCOLIGADA=Z.CODCOLIGADA  AND K.CODFILIAL=Z.CODFILIAL  AND K.CODORDEM=Z.CODORDEM  INNER JOIN KATVORDEM KA ON  KA.CODCOLIGADA=Z.CODCOLIGADA  AND KA.CODFILIAL=Z.CODFILIAL  AND KA.CODORDEM=Z.CODORDEM  AND KA.CODATIVIDADE=Z.CODATIVIDADE  INNER JOIN KESTRUTURA KE ON   KE.CODCOLIGADA = KA.CODCOLIGADA  AND KE.CODFILIAL = KA.CODFILIAL   AND KE.CODESTRUTURA = KA.CODESTRUTURA  INNER JOIN TPRD T ON  T.CODCOLIGADA=Z.CODCOLIGADA  AND T.CODIGOPRD=Z.CODIGOMP  INNER JOIN TPRDLOC E ON   E.CODCOLIGADA = Z.CODCOLIGADA  AND E.CODFILIAL = Z.CODFILIAL  AND E.IDPRD = T.IDPRD  AND E.CODLOC IN ('20','21','22','33','34')  INNER JOIN TLOTEPRD TL ON    TL.CODCOLIGADA=E.CODCOLIGADA   AND TL.IDPRD=E.IDPRD   AND TL.IDLOTE=Z.IDLOTE   AND TL.IDSTATUS=6  INNER JOIN TLOTEPRDLOC E2 ON   E2.CODCOLIGADA = Z.CODCOLIGADA  AND E2.CODFILIAL = Z.CODFILIAL  AND E2.IDPRD = T.IDPRD  AND E2.CODLOC IN ('20','21','22','33','34')  AND E2.IDLOTE=Z.IDLOTE  WHERE Z.NUMPLANOCORTE='PA08502' AND Z.CODFILIAL=7 ORDER BY CODORDEM


